import React, { useState } from 'react' // useState를 사용하기 위해 먼저 import를 진행 합니다
import styled from "styled-components";

const Login = () => {

  const [state, setState] = useState('red') // 내가 사용할 상태를 선언 해줍니다.
  // 첫번째 인자 (state)는 일반적인 변수라고 생각하시면 됩니다.
  // 두번째 인자 (setState)는 첫번째 인자의 상태(혹은 값)를 변경시킬 수 있는 함수입니다. 첫번째 인자와 같은 이름을 쓰되 앞에 set을 붙여주고 camelCase로 표기해줍니다.
  // useState() 괄호 안에는 이 상태의 초기값을 설정해주는 값을 넣어줍니다 (boolean, number, string, null, undefined)

  // ex ) const [apple, setApple] = useState(flase)
  // ex ) const [potato, setPotato] = useState(undefined)
  return (
    <LoginStyle>
      <button onClick={() => setState('red')}>빨간 버튼</button>
      <button onClick={() => setState('blue')}>파란 버튼</button>
      {state === 'red' ? 
      <div className='red'>빨간 버튼이 활성화 됐습니다.</div> :
      <div className='blue'>파란 버튼이 활성화 됐습니다.</div>}
    </LoginStyle>
  )
}

export default Login

const LoginStyle = styled.div`
  height: 50vh;
  display: flex;
  justify-content: center;
  align-items: center;

  button{
    width: 150px;
    height: 50px;
    margin: 0 30px;
  }

  .red{
    width: 50vw;
    height: 30vh;
    background-color: red;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white;
    font-weight: bold;
    font-size: 20px;
  }

  .blue{
    width: 50vw;
    height: 30vh;
    background-color: blue;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white;
    font-weight: bold;
    font-size: 20px;
  }
`
